import gmpy2
from gmpy2 import mpz

# first assignment

N=mpz()

A = gmpy2.isqrt(N)+1

tmp = A*A-N

x = gmpy2.isqrt(tmp)

p = A - x
q = A + x
if p*q == N:
  print("first factoring challenge")
  print("p=",p)

# second assignment

N=mpz()

A = gmpy2.isqrt(N)

while True:
  
  A += 1

  tmp = A*A-N
  
  if not gmpy2.is_square(tmp):
    continue
  
  x = gmpy2.isqrt(tmp)
  
  p = A - x
  q = A + x
  
  if p*q == N:
    print("second factoring challenge")
    print("p=",p)
    break

# third assignment




B = gmpy2.isqrt(24*N) + 1

tmp = B*B-24*N

woop = gmpy2.iroot(tmp,2)
if woop[1] == True:
  x = woop[0]
else:
  print("not a square")
  quit()

tmp = B - x

if not tmp%3:
  p = gmpy2.divexact(tmp,6)
  q = gmpy2.divexact(B+x,4)
else:
  p = gmpy2.divexact(tmp,4)
  q = gmpy2.divexact(B+x,6)

if p*q == N:
  print("third factoring challenge")
  print("p=",p)

# fourth assignment

print("-----------------")

cb=mpz()

e = 65537

N=mpz()

A = gmpy2.isqrt(N)+1

tmp = A*A-N

x = gmpy2.isqrt(tmp)

p = A - x
q = A + x
if p*q == N:
  print("N succesfully factored")
  #print("p=",p)

phi = (p-1)*(q-1)

d = gmpy2.powmod(e,-1,phi)

pb = gmpy2.powmod(cb,d,N)
pbh = pb.digits(16)

ph = (pbh.split("00"))[1]

print(ph)
pt = bytes.fromhex(ph)
print(pt.decode('utf-8'))

